formulas.expression:
IF(
   OR(
      /* Case 1: Today’s FQ is between 1 and 3, and Record FQ matches today’s or next, and FY = 2026 */
      AND(
         {!GettodaysFQ} >= 1,
         {!GettodaysFQ} <= 3,
         VALUE($Record.Fiscal_Year__c) = 2026,
         OR(
            VALUE($Record.Fiscal_Quarter__c) = {!GettodaysFQ},
            VALUE($Record.Fiscal_Quarter__c) = {!GettodaysFQ} + 1
         )
      ),

      /* Case 2: Today is Q4 FY26 → Record must be either FY26Q4 or FY27Q1 */
      AND(
         {!GetTodaysFiscalQuarter}="FY26Q4",
        OR(
         AND(
            VALUE($Record.Fiscal_Quarter__c) = {!GettodaysFQ},
            VALUE($Record.Fiscal_Year__c) = 2026
         ),
         AND(
            VALUE($Record.Fiscal_Quarter__c) = 1,
            VALUE($Record.Fiscal_Year__c) = 2027
         )
      )
     )
   ),
   TRUE,
   FALSE
)
