formulas.expression:
IF(
	AND(
		{!ProgramTypeValue} !="Teaming Partner",
        {!ProgramTypeValue} !="Teaming",
		{!ProgramTypeValue} !="Paid Referral",
		{!ProgramTypeValue} !="Unpaid Referral"
	),
	IF(
		{!ProgramTypeValue} == "Safety Program",
		IF(
			OR(
				ISNULL({!thisOpportunity.Insurance_Partner__c}),
				ISBLANK({!thisOpportunity.Insurance_Partner__c})
			),
			{!thisPartnerSFDC.Id},
			{!thisOpportunity.Insurance_Partner__c}
		),
		{!thisPartnerSFDC.Id}
	),
	NULL
)
