ValidationRule.errorConditionFormula:
AND(
    $RecordType.DeveloperName = "Partner",
    ISCHANGED(OwnerId),
    NOT(
        OR(
            Owner.Profile.Name = "Samsara Channel Account Managers",Owner.Profile.Name = "Samsara - Product Management",Owner.Profile.Name = "Sales Ops Profile",
            Owner.RR_Is_Pool_User__c = TRUE
        )
    )
)
