WebLink.url:
var Celigo = {};
Celigo.SFDC = (function() {
  var getCustomerFinancials = function() {
	this.triggerSync = function() {						
		try{
			var top = screen.height - (screen.height * .7) - 80,
            	left = screen.width - (screen.width * .65) - 87;
			var params = 'dependent = yes,resizable=false,scrollbars=false,toolbar=false,menubar=false,location=false,status=true,directories=false,width=575,height=200';
			params+=',top='+top.toString()+' ,left='+left.toString()+ '\'';

			var nsCustomerId  = '{!Account.netsuite_conn__NetSuite_Id__c}';				
			if(!nsCustomerId){
				alert('No Netsuite Customer associated with this account. Cannot fetch Financial Information.');
				return;
			}
			var accId = '{!Account.Id}';
			var popup_window = window.open('/apex/netsuite_conn__PullCustomerFinancials?id='+'{!Account.Id}'+'&nsId='+nsCustomerId, 'NetSuite_Synchronization'+ accId, params);
			//wait for 60 seconds or till window is closed.
			var waitcount =0;
			var intervalId = window.setInterval(function(){	
				if(waitcount>=60 || popup_window.closed){
					window.clearInterval(intervalId);
					window.location.reload();
				}
				waitcount++;
			},1000);

		}catch(e){
			console.log('The error details:'+e.message);
		}

		};
	};

	return {
		main: function() {
			Celigo.getCustomerFinancials = new getCustomerFinancials();
			Celigo.getCustomerFinancials.triggerSync();
	}

	};
})();
Celigo.SFDC.main();
