WebLink.url:
{!REQUIRESCRIPT("/soap/ajax/63.0/connection.js")}
var issue = new sforce.SObject("Issue__c");

//alert("Issue ID: " + issue.Name);

issue.Issue_Subject__c = "{!Case.Subject}";
issue.Support_Notes__c = "(Related case: {!Case.CaseNumber} - {!Case.Subject}) <br/><br/> Expected Behavior: <br/><br/> Observed Behavior: <br/><br/> Steps to Replicate:";
var result = sforce.connection.create([issue]);

if(result[0].getBoolean("success"))
{
  window.location = "/" + result[0].id + "/e";
}
  else
{
  alert('Could not create record '+result);
}
