ValidationRule.errorConditionFormula:
AND($Profile.Name = 'Samsara Channel Account Managers',
    ISPICKVAL(Business_Unit__c, 'Industrial'), 
    OR(ISPICKVAL(Industry, ''),
       AND(NOT(ISPICKVAL(Industry, 'Manufacturing')), 
           NOT(ISPICKVAL(Industry, 'Remote Infrastructure'))
       )
    )
)
