WebLink.url:
var Celigo = {};
	Celigo.SFDC = (function() {
		var opportunitySyncManager = function() {
				this.triggerSync = function() {
				this.openPopup();
			};

			this.openPopup = function() {

				var top = screen.height - (screen.height * .7) - 80,
				left = screen.width - (screen.width * .65) - 87,
				params = 'dependent = yes,resizable=yes,scrollbars=false,toolbar=false,menubar=false,location=false,status=true,directories=false,width=575,height=200,top=';

				params += top.toString();
				params += ',left=' + left.toString() + '\'';

				{!REQUIRESCRIPT("/soap/ajax/28.0/connection.js")}
				var resultOppStting = sforce.connection.query("select netsuite_conn__Account_Id__c from netsuite_conn__Opportunity_Sync_Settings__c where Name = \'Opportunity Sync Settings\'");
				var rocordsOppStting = resultOppStting.getArray("records");
				var selectedId = (rocordsOppStting[0].netsuite_conn__Account_Id__c).split('$$$')[0];
				var selectedValue = (rocordsOppStting[0].netsuite_conn__Account_Id__c).split('$$$')[1];
				var oppRecQuery = "select "+selectedId+" from Opportunity where Id = '"+ "{!Opportunity.Id}" +"'";
				var oppRec = sforce.connection.query(oppRecQuery);
				var resultOppRec = oppRec.getArray("records");
				if (!resultOppRec[0][selectedId]) {
					alert("Please select "+selectedValue+" on the Opportunity to start the sync.");
					return;
				}
				if ("{!Opportunity.netsuite_conn__Originated_From_NetSuite__c}" === '1') {
					alert("This Opportunity is created from NetSuite Sales Order, it can not create another Sales Order.");
					return;
				}
				var oppId = "{!Opportunity.Id}";
				var popup_window = window.open('/apex/netsuite_conn__Opportunity_Sync?whence='+"{!Opportunity.Id}", 'NetSuite_Synchronization'+ oppId, params);
				var intervalId = window.setInterval(function(){	
					if(popup_window.closed){ 
						window.clearInterval(intervalId); 
						window.location.reload(); 
					} 
				},1000); 
			};
		};

		return {
			main : function() {
				Celigo.opportunitySyncMgr = new opportunitySyncManager();
				Celigo.opportunitySyncMgr.triggerSync();
			}
		};
	})();
	Celigo.SFDC.main();
