WebLink.url:
{!REQUIRESCRIPT('/soap/ajax/41.0/connection.js')}

var sidebarHost = '{!$Setup.uservoice__Settings__c.uservoice__Hostname__c}';

if (!document.getElementById('uv-agent-sidebar-loader')) {
  var s = document.createElement('script');
  var t = new Date().getTime();
  s.id = 'uv-agent-sidebar-loader';
  s.setAttribute('data-subdomain', sidebarHost);
  s.setAttribute('data-bookmarklet-version', 4);
  s.setAttribute('type', 'text/javascript');
  s.onload = function () { window.uvAgentSidebar.init(); };
  s.src = "https://" + sidebarHost + "/clients/agent/sidebar_loader.js?t=" + t;
  document.body.appendChild(s);
} else {
  window.uvAgentSidebar.init();
}
