WebLink.url:
var Celigo = {};
	Celigo.SFDC = (function() {
		var opportunitySyncManager = function() {
			this.triggerSync = function() {
			this.openPopup();
			};

			this.openPopup = function() {
				var top = screen.height - (screen.height * .7) - 80,
				left = screen.width - (screen.width * .65) - 87,
				params = 'dependent = yes,resizable=yes,scrollbars=false,toolbar=false,menubar=false,location=false,status=true,directories=false,width=575,height=200,top=';

				params += top.toString();
				params += ',left=' + left.toString() + '\'';


				{!REQUIRESCRIPT("/soap/ajax/32.0/connection.js")} 
				var resultOppStting = sforce.connection.query("select netsuite_conn__Account_Id__c from netsuite_conn__Opportunity_Sync_Settings__c where Name = \'Opportunity Sync Settings\'");
				var rocordsOppStting = resultOppStting.getArray("records");
				var selectedId = (rocordsOppStting[0].netsuite_conn__Account_Id__c).split('$$$')[0];
				var selectedValue = (rocordsOppStting[0].netsuite_conn__Account_Id__c).split('$$$')[1];

				var oppId_results = sforce.connection.query("select OpportunityId from Quote where Id ='{!Quote.Id}'"); 
				var oppId = oppId_results.getArray("records")[0]['OpportunityId']; 

				var opp_results = sforce.connection.query("select Id,SyncedQuoteId,StageName,netsuite_conn__Originated_From_NetSuite__c,"+selectedId+" from Opportunity where Id ='" + oppId + "'");
				var opportunityRecord = opp_results.getArray("records")[0];

				if (!opportunityRecord) {
					alert('Could not find the Opportunity related to this Quote with opportunity id: ' + oppId);
					return;
				}
				//validations on SyncedQuote
				if (!opportunityRecord.SyncedQuoteId ||
				opportunityRecord.SyncedQuoteId.toString().indexOf('{!Quote.Id}') < 0) {
					alert('The Quote is not in sync with Opportunity it is created on. NetSuite Sales Order can be created only when Quote and Opportunity are in sync.');
					return;
				}

				if (!opportunityRecord[selectedId]) {
					alert("Please select "+selectedValue+" on the Opportunity of this Quote to start the sync.");
					return;
				}
				if (opportunityRecord.netsuite_conn__Originated_From_NetSuite__c === '1') {
					alert("Opportunity on this Quote is created from NetSuite Sales Order, can not create another Sales Order.");
					return;
				}
				var quoteId = "{!Quote.Id}";
				var popup_window = window.open('/apex/netsuite_conn__Opportunity_Sync?whence='+ oppId, 'NetSuite_Synchronization'+ quoteId, params); 

				var intervalId = window.setInterval(function(){	
					if(popup_window.closed){ 
						window.clearInterval(intervalId); 
						window.location.reload(); 
					} 
				},1000); 
			};
		};
		return {
			main: function() {
				Celigo.opportunitySyncMgr = new opportunitySyncManager();
				Celigo.opportunitySyncMgr.triggerSync();
			}
		};
	})();
	Celigo.SFDC.main();
