WebLink.url:
var Celigo = {};
	Celigo.SFDC = (function() {
		var QuoteSyncManager = function() {
			this.triggerSync = function() {
				this.openPopup();
			};

			this.openPopup = function(){

				var top = screen.height - (screen.height * 0.7) - 80,
				left = screen.width - (screen.width * 0.65) - 87,
				params = 'dependent = yes,resizable=yes,scrollbars=false,toolbar=false,menubar=false,location=false,status=true,directories=false,width=575,height=200,top=';

				params += top.toString();
				params += ',left=' + left.toString() + '\'';

				{!REQUIRESCRIPT("/soap/ajax/32.0/connection.js")}

				//Flow enable/disable check starts

				var resultQuoteSetting = sforce.connection.query("select netsuite_conn__Account_Id__c from netsuite_conn__Quote_Sync_Settings__c where Name = \'Quote Sync Settings\'"); 
				var recQuoteSetting = resultQuoteSetting.getArray("records")[0];
				var selectedId = recQuoteSetting.netsuite_conn__Account_Id__c.split('$$$')[0];
				var selectedValue = recQuoteSetting.netsuite_conn__Account_Id__c.split('$$$')[1];

				var queryRTS = "select Id, integrator_da__SObject_Type__c, integrator_da__Disabled__c from integrator_da__Real_Time_Sync__c where integrator_da__SObject_Type__c = \'quote\' AND integrator_da__Connector_Id__c = \'snc\'";
				var resultRTSync = sforce.connection.query(queryRTS);
				var recordsRTSync = resultRTSync.getArray("records");

				if(recordsRTSync && recordsRTSync.length != 1 || recordsRTSync[0].integrator_da__Disabled__c == "true"){
					alert('Salesfoce Quote to Quote flow is disabled or connector is paused.');
					return;
				}

				//Flow enable/disable check ends

				var queryQuote = "select "+selectedId+" from Quote where Id = '"+ "{!Quote.Id}" +"'";
				var resQuote = sforce.connection.query(queryQuote);
				var recQuote = resQuote.getArray("records")[0];

				var isTestingModeAndEndTheFlow = false;
				var syncSpecificCustomSettingResult = sforce.connection.query("select Id, netsuite_conn__Enable_Testing_Mode__c, netsuite_conn__Criteria_Text__c from netsuite_conn__Specific_Records_Sync_Settings__c where Name = \'Specific Records Sync Settings\'", {
					onSuccess : function(syncSpecificCustomSettingResult) {
						var syncSpecificCustomSetting = syncSpecificCustomSettingResult.getArray("records");
						if (syncSpecificCustomSetting &&
						syncSpecificCustomSetting.length > 0 &&
						syncSpecificCustomSetting[0].netsuite_conn__Enable_Testing_Mode__c == "true") {
							var description = "{!JSENCODE(Quote.Description)}";
							var criteriaText = syncSpecificCustomSetting[0].netsuite_conn__Criteria_Text__c;
							if (criteriaText) {
								criteriaText = criteriaText.toLowerCase();
							}
							if (!description || 
							(description &&
							description.toLowerCase().indexOf(criteriaText) == -1)) {	
								isTestingModeAndEndTheFlow = true;
								alert("Connector is running in Testing mode. Please make sure, Description field on Account, Opportunity & Quote contains " + syncSpecificCustomSetting[0].netsuite_conn__Criteria_Text__c + ".");
							}
						}
						if (isTestingModeAndEndTheFlow === true) {
							return;
						}
						var result = sforce.connection.query("select Id from quote where Id = '"+ "{!Quote.Id}" +"'", {
							onSuccess: function(result) {
								if (selectedId == "netsuite_conn__Celigo_Account__c" && !"{!Quote.netsuite_conn__Celigo_Account__c}") {
									alert("Please select an Account on the Opportunity, then edit and save the quote and try to sync.");
									return;
								}else if(selectedId != "netsuite_conn__Celigo_Account__c" && !recQuote[selectedId]){
									alert("Please select "+selectedValue+", then edit and save the quote and try to sync.");
									return;
								}
								var records = result.getArray("records");
								var quoteId = "{!Quote.Id}";
								var popup_window = window.open('/apex/netsuite_conn__Quote_Sync?whence='+"{!Quote.Id}", 'NetSuite_Synchronization'+ quoteId, params);

								var intervalId = window.setInterval(function(){	
									if(popup_window.closed){ 
										window.clearInterval(intervalId); 
										window.location.reload(); 
									} 
								},1000); 
							},
							onFailure: function(error) {
								alert('Error occurred: ' + JSON.stringify(error));
							}
						});	
					},
					onFailure : function(error) {
						alert('Error occurred: ' + JSON.stringify(error));
						isTestingModeAndEndTheFlow = true;
					}
				}); 
			};
		};

		return {
			main : function() {
				Celigo.quoteSyncMgr = new QuoteSyncManager();
				Celigo.quoteSyncMgr.triggerSync();
			}
		};
	})();
	Celigo.SFDC.main();
