CustomField.formula:
CEILING(

/*YEAR*/
((YEAR(SBQQ__EndDate__c)-YEAR(SBQQ__StartDate__c))*12) +

/*MONTH*/
(MONTH(SBQQ__EndDate__c)-MONTH(SBQQ__StartDate__c)) +

/*DAY*/
(DAY(SBQQ__EndDate__c)-DAY(SBQQ__StartDate__c)+1)/

/*DAYS IN MONTH END DATE*/
(DAY(
DATE(
YEAR(SBQQ__EndDate__c),
IF(MONTH(SBQQ__EndDate__c) + 1=13,1,MONTH(SBQQ__EndDate__c) + 1),
1)
- 1))

)
