ValidationRule.errorConditionFormula:
AND( 
    CONTAINS($User.LastName,'Toggle'),
     OR(
        AND(
            TimeZone__c = 'America/Los_Angeles',
            TIMENOW() >= TIMEVALUE("00:00:00.000"),
            TIMENOW() <= TIMEVALUE("12:50:00.000")
            ),
        AND(
            OR(TimeZone__c = 'America/New_York',
               TimeZone__c = 'America/Panama'
               ),
            OR(TIMENOW() >= TIMEVALUE("21:00:00.000"),
               TIMENOW() <= TIMEVALUE("11:50:00.000")
               )
            ),
        AND(
            OR(CONTAINS(TimeZone__c, 'Europe'),
               CONTAINS(TimeZone__c, 'GMT')
               ),
            OR(TIMENOW() >= TIMEVALUE("17:00:00.000"),
               TIMENOW() <= TIMEVALUE("06:00:00.000")
               )
            )
        ),
    OR(ISCHANGED(Signed_In__c), 
       ISCHANGED(Inbound_ADR_Out_Of_Office__c)
       )
)
